mapper.select:
select
IFNULL(sum(a.agent_super_income),0.00)agentSuperIncome,(select IFNULL(sum(b.agent_super_income),0.00) from agent_charge b where b.status =1 and a.agent_super_id=b.agent_super_id)agentOneIncome
IFNULL(sum(a.agent_one_income),0.00)agentSuperIncome,(select IFNULL(sum(b.agent_one_income),0.00) from agent_charge b where b.status =1 and a.agent_one_id=b.agent_one_id)agentOneIncome
IFNULL(sum(a.agent_two_income),0.00)agentSuperIncome,(select IFNULL(sum(b.agent_two_income),0.00) from agent_charge b where b.status =1 and a.agent_two_id=b.agent_two_id)agentOneIncome
IFNULL(sum(a.agent_three_income),0.00)agentSuperIncome,(select IFNULL(sum(b.agent_three_income),0.00) from agent_charge b where b.status =1 and a.agent_three_id=b.agent_three_id)agentOneIncome
from agent_charge a where a.status = 0
and a.agent_super_id = #{agentSuperId}
and a.agent_one_id = #{agentOneId}
and a.agent_two_id = #{agentTwoId}
and a.agent_three_id = #{agentThreeId}

select

	IFNULL(sum(agent_super_income),0.00)agentSuperIncome


	IFNULL(sum(agent_one_income),0.00)agentSuperIncome


	IFNULL(sum(agent_two_income),0.00)agentSuperIncome


	IFNULL(sum(agent_three_income),0.00)agentSuperIncome

from agent_charge where TO_DAYS(NOW())-TO_DAYS(create_time) =1

	and agent_super_id = #{agentSuperId}


	and agent_one_id = #{agentOneId}


	and agent_two_id = #{agentTwoId}


	and agent_three_id = #{agentThreeId}

select a.agent_super_id agentSuperId,a.agent_one_id agentOneId,a.agent_two_id agentTwoId,a.agent_three_id agentThreeId,

	a.agent_super_income agentIncome,


	a.agent_one_income agentIncome,


	a.agent_two_income  agentIncome,


	a.agent_three_income agentIncome,

a.create_time createTime,b.member_name memberName,a.status status,b.price amount
from agent_charge a,charge_order b	where a.order_id = b.id

	and a.agent_super_id = #{agentId}


	and a.agent_one_id = #{agentId}


	and a.agent_two_id = #{agentId}


	and a.agent_three_id = #{agentId}

order by create_time DESC

select b.charge_name chargeName,b.price price,b.member_name memberName,b.create_date createDate, a.agent_super_id agentSuperId,a.agent_one_id agentOneId,a.agent_two_id agentTwoId,a.agent_three_id agentThreeId,
a.agent_super_fee agentSuperFee,a.agent_super_income*0.01 agentSuperIncome, "-" agentOneFee,"-" agentOneIncome,"-" agentTwoFee,"-" agentTwoIncome,"-" agentThreeFee,"-" agentThreeIncome
a.agent_one_fee agentOneFee,a.agent_one_income*0.01 agentOneIncome, "-" agentSuperFee,"-" agentSuperIncome,"-" agentTwoFee,"-" agentTwoIncome,"-" agentThreeFee,"-" agentThreeIncome
a.agent_two_fee agentTwoFee,a.agent_two_income*0.01 agentTwoIncome, "-" agentSuperFee,"-" agentSuperIncome,"-" agentOneFee,"-" agentOneIncome,"-" agentThreeFee,"-" agentThreeIncome
a.agent_three_fee agentThreeFee,a.agent_three_income*0.01 agentThreeIncome, "-" agentSuperFee,"-" agentSuperIncome,"-" agentOneFee,"-" agentOneIncome,"-" agentTwoFee,"-" agentTwoIncome
a.agent_super_fee agentSuperFee,a.agent_super_income*0.01 agentSuperIncome, a.agent_one_fee agentOneFee,a.agent_one_income*0.01 agentOneIncome, a.agent_two_fee agentTwoFee,a.agent_two_income*0.01 agentTwoIncome, a.agent_three_fee agentThreeFee,a.agent_three_income*0.01 agentThreeIncome
from agent_charge a,charge_order b where a.order_id = b.id and b.charge_state=1
and a.agent_super_id = #{agentId}
and a.agent_one_id = #{agentId}
and a.agent_two_id = #{agentId}
and a.agent_three_id = #{agentId}
