select
sum(price)
from charge_order a LEFT JOIN t_member b on a.member_id = b.id
where 1=1

	AND a.member_id = #{memberId}


	AND a.charge_name = #{chargeName}


	AND a.charge_state = #{chargeState}


	AND  DATEDIFF(a.create_date,#{registeDate}) >= 0


	AND  DATEDIFF(a.create_date,#{endtime})<0

select a.id id, a.order_no orderNo, a.chargeruleid chargeruleid,a.charge_name chargeName, a.price price, a.member_id memberId, a.member_name memberName, a.charge_type chargeType, a.charge_state chargeState, a.coins_before coinsBefore, a.coins_after coinsAfter, a.coins_charge coinsCharge, a.coins_offer coinsOffer,a.create_date createDate, a.update_date updateDate, b.register_channel registerChannel, b.login_channel loginChannel, b.memberID memberNum,b.last_login_from lastLoginFrom, b.memberID memberNum from charge_order a LEFT JOIN t_member b on a.member_id = b.id WHERE 1=1
AND b.register_channel = #{channelNum}
AND b.last_login_from = 'android'
AND (b.last_login_from is NULL or b.last_login_from = 'iOS')
AND b.name like concat('%',#{memberName},'%')
AND a.member_id = #{memberId}
AND a.chargeruleid = #{chargeruleid}
AND a.charge_state = #{chargeState}
AND DATEDIFF(a.create_date,#{registeDate}) >= 0
AND DATEDIFF(a.create_date,#{endtime})<0
ORDER by id DESC

select a.id id, a.order_no orderNo, a.chargeruleid chargeruleid,a.charge_name chargeName, a.price price, a.member_id memberId, a.member_name memberName, a.charge_type chargeType, a.charge_state chargeState, a.coins_before coinsBefore, a.coins_after coinsAfter, a.coins_charge coinsCharge, a.coins_offer coinsOffer,a.create_date createDate, a.update_date updateDate, b.register_channel registerChannel, b.login_channel loginChannel, b.memberID memberNum,b.last_login_from lastLoginFrom, b.memberID memberNum from charge_order a LEFT JOIN t_member b on a.member_id = b.id WHERE 1=1 and b.register_channel in ('an1','an2','an3','an4','an5','an6','an7') and a.member_id not in (SELECT user_id from member_channel_deduct)
AND b.register_channel = #{channelNum}
AND b.last_login_from = 'android'
AND (b.last_login_from is NULL or b.last_login_from = 'iOS')
AND b.name like concat('%',#{memberName},'%')
AND a.member_id = #{memberId}
AND a.chargeruleid = #{chargeruleid}
AND a.charge_state = #{chargeState}
AND DATEDIFF(a.create_date,#{registeDate}) >= 0
AND DATEDIFF(a.create_date,#{endtime})<0
ORDER by id DESC

