mapper.select:
select
from t_member a LEFT JOIN (SELECT NULLIF(COUNT(id),0) jicun,order_by FROM t_doll_order WHERE STATUS = '寄存中' GROUP BY order_by) b ON a.id = b.order_by LEFT JOIN account c on a.id = c.id where 1=1
and a.id = #{id}
and a.name like CONCAT('%',#{userName},'%')
and a.mobile = #{phone}
and DATEDIFF(a.register_date,#{beginTime}) = 0
AND a.phone_model = #{phoneModel}
order by a.register_date DESC

select
from t_member where 1=1
and memberID = #{memberId}

SELECT coins
FROM account
WHERE id = #{id}
