mapper.select:
select
id, username, password, phone, nick_name AS nickName, level, status, agent_id AS agentId, agent_one_id AS agentOneId, agent_two_id AS agentTwoId, create_time AS createTime, update_time AS updateTime, fee

	,(balance-balance_disabled)*0.01 AS balance


	,"-" AS balance

from  t_agent where 1=1

	and agent_id = #{agentId}


	and agent_one_id = #{agentId}


   and agent_two_id = #{agentId}


	and nick_name = #{nickName}


	and username = #{username}


	and phone = #{phone}


	and level = #{level}


	and DATEDIFF(create_time,#{createTime}) >= 0
