mapper.select:
		select id,name,description,machine_type machineType, quantity, price, machine_status machineStatus,
		tbimg_real_path tbimgRealPath,modified_date modifiedDate,
		modified_by modifiedBy,watching_number watchingNumber,redeem_coins
		redeemCoins, timeout, machine_code machineCode,doll_ID dollID from t_doll
		where machine_status!="" AND machine_status!="已下架" and delete_status = 1

            and id = #{dollId}


			and name like CONCAT('%',#{name},'%')


            and machine_code = #{machineCode}



				AND (machine_status = '空闲中' or machine_status = '游戏中')


				and machine_status = #{machineStatus}



			and machine_type = #{machineType}



				and modified_by IS NULL


        order by modified_date desc
