mapper.select:
select m.name,a.id id,a.order_date orderDate, a.order_by orderBy,a.status status,a.modified_date modifiedDate, b.receiver_phone addrPhone,b.receiver_name addrName, b.province province, b.city city, b.county county, b.street street,d.doll_name dollName,d.doll_url dollUrl,d.quantity quantity from t_doll_order a LEFT JOIN t_doll_order_item d on a.id = d.order_id LEFT JOIN t_member_addr b ON a.address_id=b.id LEFT JOIN t_member m ON a.order_by=m.id where a.status='申请发货'
and a.order_by = #{id}
and b.receiver_phone = #{phone}
and b.receiver_name like concat('%',#{addrName},'%')
and d.doll_name like concat('%',#{dollName},'%')
order by a.modified_date DESC,a.order_by DESC

select m.name,a.id id,a.order_date orderDate, a.order_by orderBy,a.status status,a.modified_date modifiedDate, b.receiver_phone addrPhone,b.receiver_name addrName, b.province province, b.city city, b.county county, b.street street,d.doll_name dollName,d.doll_url dollUrl,d.quantity quantity from t_doll_order a LEFT JOIN t_doll_order_item d on a.id = d.order_id LEFT JOIN t_member_addr b ON a.address_id=b.id LEFT JOIN t_member m ON a.order_by=m.id where a.status='待发货'
and a.order_by = #{id}
and b.receiver_phone = #{phone}
and b.receiver_name like concat('%',#{addrName},'%')
and d.doll_name like concat('%',#{dollName},'%')
order by a.modified_date DESC,a.order_by DESC

select m.name,a.id id,a.order_date orderDate, a.order_by orderBy,a.status status,a.deliver_date deliverDate,a.deliver_amount deliverAmount, a.deliver_method deliverMethod, a.deliver_number deliverNumber,a.modified_date modifiedDate, b.receiver_phone addrPhone,b.receiver_name addrName, b.province province, b.city city, b.county county, b.street street,d.doll_name dollName,d.doll_url dollUrl,COUNT(a.id) quantity from t_doll_order a LEFT JOIN t_member_addr b ON a.address_id=b.id LEFT JOIN t_doll_order_item d on a.id = d.order_id LEFT JOIN t_member m ON a.order_by=m.id where a.status='已发货'
and a.order_by = #{id}
and b.receiver_phone = #{phone}
and b.receiver_name like concat('%',#{addrName},'%')
and d.doll_name like concat('%',#{dollName},'%')
GROUP BY a.deliver_number,d.doll_id ORDER BY a.modified_date DESC,a.order_by DESC

